components/amp/css/footer:
.amp-footer {
    color: #989898;
    text-align: center;
    border-top: 1px solid #e8e8e8
}

.amp-footer a {
    text-decoration: underline;
}
.amp-footer a:hover {
    color: #4f4f4f
}

.amp-footer-heading {
    text-transform: capitalize;
    font-weight: bold;
}

.amp-footer-list {
    margin: 0;
    list-style-type: none;
    display: block;
    padding: 0 .75rem;
}

.amp-footer-list li {
    padding: 0.375rem .5rem;
    display: inline-block;
}

.powered-by {
    margin: 3rem 0 .75rem
}
.powered-by + .powered-by {
    margin-top: 0
}
.powered-by:last-child {
    margin-bottom: 1.5rem
}
