components/common/navigation-list:
{{#if children}}
<a class="navPages-action has-subMenu{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}"
    {{#if theme_settings.navigation_hover}}
        data-collapsible-disabled-breakpoint="large"
        data-collapsible-disabled-state="open"
        data-collapsible-enabled-state="closed"
    {{/if}}
>
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    {{#if @root.in_development}}
        <div data-content-region="navPages_{{id}}_left--global">
            <img src="https://via.placeholder.com/150x150">
        </div>
    {{else}}
        {{{region name=(concat (concat "navPages_" id) "_left--global")}}}
    {{/if}}
    <ul class="navPage-subMenu-list">
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{{lang 'category.view_all.name' category=name}}}</a>
        </li>
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if @root.in_development}}
                    <div data-content-region="navPages_{{id}}_top--global">
                        <img src="https://via.placeholder.com/220x100">
                    </div>
                {{else}}
                    {{{region name=(concat (concat "navPages_" id) "_top--global")}}}
                {{/if}}
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu{{#if is_active}} activePage{{/if}}"
                        href="{{url}}"
                        data-collapsible="navPages-{{id}}"
                        data-collapsible-disabled-breakpoint="medium"
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
                {{/if}}
                {{#if @root.in_development}}
                    <div data-content-region="navPages_{{id}}_bottom--global">
                        <img src="https://via.placeholder.com/220x100">
                    </div>
                {{else}}
                    {{{region name=(concat (concat "navPages_" id) "_bottom--global")}}}
                {{/if}}
            </li>
        {{/each}}
    </ul>
    {{#if @root.in_development}}
        <div data-content-region="navPages_{{id}}_right--global">
            <img src="https://via.placeholder.com/150x150">
        </div>
    {{else}}
        {{{region name=(concat (concat "navPages_" id) "_right--global")}}}
    {{/if}}
</div>
{{else}}
<a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action{{#unless ../noDepthMaxClass}} navPages-action-depth-max{{/unless}} has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}{{#if ../idSuffix}}{{../../idSuffix}}{{/if}}"
        {{#if theme_settings.navigation_hover}}
            data-collapsible-disabled-breakpoint="large"
            data-collapsible-disabled-state="open"
            data-collapsible-enabled-state="closed"
        {{/if}}
    >
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown idSuffix=../idSuffix noDepthMaxClass=../noDepthMaxClass}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt socialLinks--colors">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/common/navigation-page-list:
{{#if children}}
    <a class="navPages-action{{#if is_root}} is-root{{else}} navPage-subMenu-action{{/if}}{{#unless noDepthMaxClass}} navPages-action-depth-max{{/unless}} has-subMenu" href="{{url}}" data-collapsible="navPages-page-{{dashcase name}}{{idSuffix}}"
        {{#if theme_settings.navigation_hover}}
            data-collapsible-disabled-breakpoint="large"
            data-collapsible-disabled-state="open"
            data-collapsible-enabled-state="closed"
        {{/if}}
    >
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    <div class="navPage-subMenu navPage-subMenu-horizontal" id="navPages-page-{{dashcase name}}{{idSuffix}}" aria-hidden="true" tabindex="-1">
        <ul class="navPage-subMenu-list">
            <li class="navPage-subMenu-item-parent">
                <a class="navPage-subMenu-action navPages-action{{#unless noDepthMaxClass}} navPages-action-depth-max{{/unless}}" href="{{url}}">{{name}}</a>
            </li>
            {{#each children}}
                <li class="navPage-subMenu-item-child">
                    {{> components/common/navigation-page-list theme_settings=../theme_settings idSuffix=../idSuffix noDepthMaxClass=../noDepthMaxClass}}
                </li>
            {{/each}}
        </ul>
    </div>
{{else}}
    <a class="navPages-action{{#if is_root}} is-root{{else}} navPage-subMenu-action{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}
