pages/product:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
---
{{#partial "page-before"}}
    <div id="productpage-banner-top">
    </div>
{{/partial}}
{{#partial "page"}}

    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

    {{#each product.shipping_messages}}
        {{> components/common/alert-info message}}
    {{/each}}

    <div class="productView-container">
        <div itemscope itemtype="http://schema.org/Product">
            {{> components/products/product-view schema=true}}
        </div>

        {{{region name="product_below_content--global"}}}
        {{{region name="product_below_content"}}}

        {{#any (unless theme_settings.alsobought) (if theme_settings.alsobought_product '!=' 'related')}}
        {{#if product.related_products}}
        <section class="productView-productsList productView-productsList--related">
            <h3 class="productView-productsList-heading">{{lang 'products.related_products'}}</h3>
            <div class="productView-productsList-content">
                {{> components/products/carousel products=product.related_products columns=5 list='Related Products'}}
            </div>
        </section>
        {{/if}}
        {{/any}}

        {{#any (unless theme_settings.alsobought) (if theme_settings.alsobought_product '!=' 'similar')}}
        {{#if product.similar_by_views}}
        <section class="productView-productsList productView-productsList--similar">
            <h3 class="productView-productsList-heading">{{lang 'products.similar_by_views'}}</h3>
            <div class="productView-productsList-content">
                {{> components/products/carousel products=product.similar_by_views columns=5 list='Customers Also Viewed'}}
            </div>
        </section>
        {{/if}}
        {{/any}}
    </div>

{{/partial}}
{{> layout/base}}

components/common/header:
{{#if banners}}
{{> components/chiara/banners/count-standard-banners banners=banners}}
{{#if banners.top_count}}
<div class="banners{{#if theme_settings.topHeader_show}}{{#if theme_settings.topHeader_show_banner}} u-hiddenVisually-desktop{{/if}}{{/if}}" data-banner-location="top">
    {{#each banners.top_metadata}}
        {{#unless (occurrences this.content 'data-remote-banner-position')}}
            {{#unless (occurrences this.content 'data-local-banner-position')}}
                <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-banner-id="{{this.banner-name}}">
                    <div data-event-type="promotion-click">
                        {{{this.content}}}
                    </div>
                </div>
            {{/unless}}
        {{/unless}}
    {{/each}}
</div>
{{/if}}
{{/if}}
<header class="header" role="banner"{{#if theme_settings.header_sticky}} data-stickymenu{{/if}}>
    <!--
    <a class="mobileMenu-toggle" data-mobile-menu-toggle="menu">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>
    -->

    {{#if theme_settings.topHeader_show}}
    <div class="header-top">
        <div class="header-top-container">
            <ul class="header-top-list">
                {{#if theme_settings.topHeader_show_phone}}
                    {{#if theme_settings.contact_phone}}
                        {{#if theme_settings.contact_phoneText}}
                            <li class="header-top-item header-top-item--phone"><a class="header-top-action" href="tel:{{theme_settings.contact_phone}}">{{{theme_settings.contact_phoneText}}}</a></li>
                        {{else}}
                            <li class="header-top-item header-top-item--phone"><a class="header-top-action" href="tel:{{theme_settings.contact_phone}}">{{lang 'footer.call_us' phone_number=theme_settings.contact_phoneText}}</a></li>
                        {{/if}}
                    {{else}}
                        {{#if settings.phone_number}}
                            {{#if theme_settings.contact_phoneText}}
                                <li class="header-top-item header-top-item--phone"><a class="header-top-action" href="tel:{{settings.phone_number}}">{{{theme_settings.contact_phoneText}}}</a></li>
                            {{else}}
                                <li class="header-top-item header-top-item--phone"><a class="header-top-action" href="tel:{{settings.phone_number}}">{{lang 'footer.call_us' phone_number=settings.phone_number}}</a></li>
                            {{/if}}
                        {{/if}}
                    {{/if}}
                {{/if}}

                {{#and theme_settings.topHeader_show_address settings.address}}
                <li class="header-top-item header-top-item--address"><a class="header-top-action">{{settings.address}}</a></li>
                {{/and}}

                {{#if theme_settings.topHeader_show_banner}}
                {{#if banners.top_count}}
                <li class="header-top-item header-top-item--banner">
                    {{#each banners.top_metadata}}
                        {{#unless (occurrences this.content 'data-remote-banner-position')}}
                            {{#unless (occurrences this.content 'data-local-banner-position')}}
                                <div class="banner">
                                    {{{this.content}}}
                                </div>
                            {{/unless}}
                        {{/unless}}
                    {{/each}}
                </li>
                {{/if}}
                {{/if}}
                {{#if currency_selector.currencies.length '>' 1}}
                <li class="header-top-item header-top-item--currency">
                    <a class="header-top-action header-top-action--currencySelector has-dropdown" href="#" data-dropdown="header-top-currencySelection" data-options="align:top" aria-controls="header-top-currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
                    <ul class="dropdown-menu" id="header-top-currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
                        {{#each currency_selector.currencies}}
                        <li class="dropdown-menu-item">
                            <a href="{{{switch_url}}}"
                                data-currency-code="{{{code}}}"
                                {{#if cart_currency_switch_url.length '>' 0 }}
                                data-cart-currency-switch-url="{{{cart_currency_switch_url}}}"
                                {{/if}}
                                data-warning="{{lang 'common.currency_switch_promotion'}}"
                            >
                            {{#if is_active}}
                                <strong>{{name}}</strong>
                            {{else}}
                                {{name}}
                            {{/if}}
                            </a>
                        </li>
                        {{/each}}
                    </ul>
                </li>
                {{/if}}

                {{#if customer}}
                    <li class="header-top-item header-top-item--account">
                        <a class="header-top-action" href="{{urls.account.index}}">{{lang 'common.account'}}</a>
                    </li>
                    <li class="header-top-item header-top-item--logout">
                        <a class="header-top-action" href="{{urls.auth.logout}}">{{lang 'common.logout'}}</a>
                    </li>
                {{else}}
                    {{#if settings.account_creation_enabled}}
                    <li class="header-top-item header-top-item--createAccount">
                        <a class="header-top-action" href="{{urls.auth.create_account}}">{{lang 'login.new_customer.create_account' }}</a>
                    </li>
                    {{/if}}
                    <li class="header-top-item header-top-item--login">
                        {{#if theme_settings.navUser_panel}}
                            <a class="header-top-action" data-collapsible="navUser-more-panel" data-group-collapsible="main">{{lang 'login.heading' }}</a>
                        {{else}}
                            <a class="header-top-action" href="{{urls.auth.login}}">{{lang 'login.heading' }}</a>
                        {{/if}}
                    </li>
                {{/if}}
            </ul>
        </div>
    </div>
    {{/if}}

    <div class="header-main">
        <div class="header-main-container header-main-container--{{theme_settings.logo-position}}">
            {{#all (if page_type '===' 'default') (unless settings.store_logo.image)}}
                <h1 class="header-logo header-logo--{{theme_settings.logo-position}}">
                    {{> components/common/store-logo}}
                </h1>
            {{else}}
                <div class="header-logo header-logo--{{theme_settings.logo-position}}">
                    {{> components/common/store-logo}}
                </div>
            {{/all}}

            <div class="navPages-container" id="menu" data-menu>
                <p class="navPages-container-heading"><span class="navPages-container-heading-ellipse">{{lang 'bottom_bar.more'}}</span></p>
                <a class="mobile-panel-close" data-mobile-menu-toggle="menu" href="#"><svg><use xlink:href="#icon-close" aria-hidden="true" /></svg></a>
                {{> components/common/navigation-menu}}
            </div>

            {{> components/common/navigation}}
        </div>
    </div>
</header>

{{{region name="header_bottom--global"}}}
{{{region name="header_bottom"}}}

<div class="dropdown dropdown--quickSearch" id="quickSearch" aria-hidden="true" tabindex="-1" data-prevent-quick-search-close>
    {{> components/common/quick-search}}
</div>

components/products/product-view:
{{! COUNT CUSTOM FIELDS: }}
{{#if theme_settings.productpage_custom_fields_hide_special}}
    {{#if (merge product custom_fields_count=0)}}{{/if}}
    {{#each product.custom_fields}}
        {{#startsWith '__' name}}
        {{else}}
            {{#if (merge ../../product custom_fields_count=(add ../../product.custom_fields_count 1))}}{{/if}}
        {{/startsWith}}
    {{/each}}
{{else}}
    {{#if (merge product custom_fields_count=product.custom_fields.length)}}{{/if}}
{{/if}}

<div class="productView" {{#if settings.data_tag_enabled}} data-event-type="product" data-entity-id="{{product.id}}" data-name="{{product.title}}" data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{product.brand.name}}" data-product-price="{{#if product.price.with_tax}}{{product.price.with_tax.value}}{{else}}{{product.price.without_tax.value}}{{/if}}" data-product-variant="single-product-option"{{/if}}>
<div data-also-bought-parent-scope data-product class="productView-outerAlsoBought">
    {{#each product.reviews.messages}}
        {{#if error}}
            {{> components/common/alert-error error}}
        {{/if}}
        {{#if success}}
            {{> components/common/alert-success success}}
        {{/if}}
    {{/each}}

    <section class="productView-details">


        <div class="productView-priceShareWrapper">
            <div class="productView-price">
                {{#if product.call_for_price}}
                    <span class="price price--call">{{product.call_for_price}}</span>
                {{else}}
                    {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                        {{> components/products/price price=product.price schema_org=schema}}
                    {{else}}
                        {{> components/common/login-for-pricing}}
                    {{/or}}
                {{/if}}
            </div>

            <div class="productView-otherButtons">
                {{#if settings.show_wishlist}}
                <div class="productView-wishlist">
                    {{> components/common/wishlist-dropdown}}
                </div>
                {{/if}}
                <div class="productView-share">
                    <a class="productView-share-toggle" href="#" data-dropdown="productView-share-dropdown-{{product.id}}">
                        <span class="is-srOnly">{{lang 'common.share'}}</span>
                        <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-share" /></svg></i>
                    </a>
                    <div class="productView-share-dropdown" data-dropdown-content id="productView-share-dropdown-{{product.id}}">
                        {{>components/common/share url=product.url}}
                    </div>
                </div>
            </div>
        </div>
        {{{region name="product_below_price--global"}}}
        {{{region name="product_below_price"}}}

        <h1 class="productView-title" {{#if schema}}itemprop="name"{{/if}}>{{product.title}}</h1>

        {{#if product.brand}}
        <h5 class="productView-brand"{{#if schema}} itemprop="brand" itemscope itemtype="http://schema.org/Brand"{{/if}}>
            <a href="{{product.brand.url}}"{{#if schema}} itemprop="url"{{/if}}><span{{#if schema}} itemprop="name"{{/if}}>{{product.brand.name}}</span></a>
        </h5>
        {{/if}}

        <section class="productView-banner productView-banner--5" id="chiara-productpage-banner5"></section>

        {{#and schema product.sku}}
            <meta itemprop="sku" content="{{product.sku}}" />
        {{/and}}

        {{#and schema product.mpn}}
            <meta itemprop="mpn" content="{{product.mpn}}" />
        {{/and}}

        {{#and schema product.gtin}}
            <meta itemprop="gtin{{length product.gtin}}" content="{{product.gtin}}" />
        {{/and}}

        {{#block "chiara-productpage-rating"}}
        <div class="productView-rating"{{#if schema}}{{#if settings.show_product_rating}}{{#if product.num_reviews '>' 0}} itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating"{{/if}}{{/if}}{{/if}}>
            {{#if settings.show_product_rating}}
                {{#if product.num_reviews '>' 0}}
                    {{#if schema}}
                        <meta itemprop="ratingValue" content="{{product.rating}}">
                        <meta itemprop="ratingCount" content="{{product.num_reviews}}">
                        <meta itemprop="reviewCount" content="{{product.num_reviews}}">
                    {{/if}}
                {{/if}}
                {{> components/products/ratings rating=product.rating}}
                <span class="productView-reviewLink">
                    {{#if product.num_reviews '>' 0}}
                        <a href="{{product.url}}#product-reviews" 
                            data-collapsible="productView-reviews-{{product.id}}"
                            data-collapsible-disabled-breakpoint="medium"
                            data-collapsible-disabled-state="closed"
                            data-group-collapsible="main">
                            {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                        </a>
                    {{else}}
                        {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                    {{/if}}
                </span>
            {{/if}}
            {{#if settings.show_product_reviews}}
                <span class="productView-reviewLink">
                    <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                        {{#unless is_ajax }}data-reveal-id="modal-review-form"{{/unless}}>
                        {{lang 'products.reviews.new'}}
                    </a>
                </span>
                {{> components/products/modals/writeReview}}
            {{/if}}

            {{{snippet 'product_details'}}}
        </div>
        {{/block}}

        {{#unless theme_settings.productpage_properties_tab}}
            <dl class="productView-info productView-info--desktopOnly">
                <dt class="productView-info-name productView-info-name--upc sku-label"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku'}}</dt>
                <dd class="productView-info-value productView-info-value--sku" data-product-sku>{{product.sku}}</dd>
                <dt class="productView-info-name productView-info-name--upc upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</dt>
                <dd class="productView-info-value productView-info-value--upc" data-product-upc>{{product.upc}}</dd>
                <dt class="productView-info-name productView-info-name--mpn mpn-label" {{#unless product.mpn}} style="display: none;"{{/unless}}>{{lang 'products.mpn'}}</dt>
                <dd class="productView-info-value productView-info-value--mpn" data-product-mpn data-original-mpn="{{product.mpn}}">{{product.mpn}}</dd>
                {{#if product.condition}}
                    <dt class="productView-info-name productView-info-name--condition">{{lang 'products.condition'}}</dt>
                    <dd class="productView-info-value productView-info-value--condition">{{product.condition}}</dd>
                {{/if}}
                {{#if product.availability}}
                    <dt class="productView-info-name productView-info-name--availability">{{lang 'products.availability'}}</dt>
                    <dd class="productView-info-value productView-info-value--availability">{{product.availability}}</dd>
                {{/if}}
                {{#all product.weight theme_settings.show_product_weight}}
                    <dt class="productView-info-name productView-info-name--weight">{{lang 'products.weight'}}</dt>
                    <dd class="productView-info-value productView-info-value--weight" data-product-weight>{{product.weight}}</dd>
                {{/all}}
                {{#all product.width product.height product.depth theme_settings.show_product_dimensions}}
                    <dt class="productView-info-name productView-info-name--height">{{lang 'products.width'}}</dt>
                    <dd class="productView-info-value productView-info-value--height" data-product-width>
                        {{product.width}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                    <dt class="productView-info-name productView-info-name--height">{{lang 'products.height'}}</dt>
                    <dd class="productView-info-value productView-info-value--height" data-product-height>
                        {{product.height}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                    <dt class="productView-info-name productView-info-name--depth">{{lang 'products.depth'}}</dt>
                    <dd class="productView-info-value productView-info-value--depth" data-product-depth>
                        {{product.depth}}
                        {{#if settings.measurements.length '==' 'Centimeters'}}
                        ({{lang 'products.measurement.metric'}})
                        {{else}}
                        ({{lang 'products.measurement.imperial'}})
                        {{/if}}
                    </dd>
                {{/all}}
                {{#if product.min_purchase_quantity}}
                    <dt class="productView-info-name productView-info-name--minQty">{{lang 'products.min_purchase_quantity'}}</dt>
                    <dd class="productView-info-value productView-info-value--minQty">{{lang 'products.purchase_units' quantity=product.min_purchase_quantity}}</dd>
                {{/if}}
                {{#if product.max_purchase_quantity}}
                    <dt class="productView-info-name productView-info-name--maxQty">{{lang 'products.max_purchase_quantity'}}</dt>
                    <dd class="productView-info-value productView-info-value--maxQty">{{lang 'products.purchase_units' quantity=product.max_purchase_quantity}}</dd>
                {{/if}}
                {{#if product.gift_wrapping_available}}
                    <dt class="productView-info-name productView-info-name--giftWrap">{{lang 'products.gift_wrapping'}}</dt>
                    <dd class="productView-info-value productView-info-value--giftWrap">{{lang 'products.gift_wrapping_available'}}</dd>
                {{/if}}
                {{#if product.shipping}}
                    {{#if product.shipping.calculated}}
                        <dt class="productView-info-name productView-info-name--shippingCalc">{{lang 'products.shipping'}}</dt>
                        <dd class="productView-info-value productView-info-value--shippingCalc">{{lang 'products.shipping_calculated'}}</dd>
                    {{else}}
                        {{#if product.shipping.price.value '===' 0}}
                            <dt class="productView-info-name productView-info-name--shippingFree">{{lang 'products.shipping'}}</dt>
                            <dd class="productView-info-value productView-info-value--shippingFree">{{lang 'products.shipping_free'}}</dd>
                        {{else}}
                            <dt class="productView-info-name productView-info-name--shippingFixed">{{lang 'products.shipping'}}</dt>
                            <dd class="productView-info-value productView-info-value--shippingFixed">{{lang 'products.shipping_fixed' amount=product.shipping.price.formatted}}</dd>
                        {{/if}}
                    {{/if}}
                {{/if}}

                {{#unless theme_settings.productpage_custom_fields_tab}}
                    {{> components/products/custom-fields}}
                {{/unless}}
            </dl>
        {{/unless}}

        {{product.detail_messages}}
    </section>

    <section class="productView-images {{#unless theme_settings.productpage_vertical_thumbnails}}productView-images--bottom{{/unless}}" data-image-gallery>
        {{#if schema}}
            {{#if product.main_image}}
                <meta itemprop="image" content="{{getImage product.main_image 'zoom_size' (cdn theme_settings.default_image_product)}}?imbypass=on" />
            {{/if}}
        {{/if}}
        {{#or product.images product.videos.list}}
        <ul class="productView-imageCarousel-main" id="productView-imageCarousel-main-{{product.id}}" data-image-gallery-main data-lightbox-type="{{theme_settings.lightbox_type}}">
            {{#each product.images}}
                <li class="productView-imageCarousel-main-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                    <a href="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on" data-zoom-image="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on" data-sizes="{{../theme_settings.zoom_size}}">
                        <span class="productView-imageCarousel-main-item-img-container">
                        {{#if @index '===' 0}}
                            <img src="{{getImage this 'product_size' (cdn ../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}">
                        {{else}}
                            <img src="{{cdn 'img/loading.svg'}}" data-lazy="{{getImage this 'product_size' (cdn ../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}">
                        {{/if}}
                        </span>
                    </a>
                </li>
            {{/each}}

            {{#if theme_settings.productpage_videos_type '===' 'gallery'}}
            {{#each product.videos.list}}
                <li class="productView-imageCarousel-main-item productView-imageCarousel-main-item--video">
                    <a
                        href="//www.youtube.com/embed/{{id}}?autoplay=1"
                        data-image-gallery-item
                        data-image-gallery-new-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                        data-image-gallery-zoom-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                        data-video-id="{{id}}">
                        <span class="productView-imageCarousel-main-item-img-container yt-video">
                            <img src="//i.ytimg.com/vi/{{id}}/maxresdefault.jpg" alt="{{title_long}}" title="{{title_long}}">
                        </span>
                    </a>
                </li>
            {{/each}}
            {{/if}}
        </ul>
        {{/or}}

        {{#if theme_settings.productpage_videos_type '===' 'gallery'}}
        {{#if product.videos.list.length}}
        <div class="productView-videoPlayer hide" data-video-player>
            <div class="productView-videoPlayer-container">
                <iframe
                    data-video-player
                    type="text/html"
                    width="815"
                    height="496"
                    frameborder="0"
                    webkitAllowFullScreen
                    mozallowfullscreen
                    allowFullScreen
                    allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                    src="//www.youtube.com/embed/{{product.videos.featured.id}}">
                </iframe>
            </div>
        </div>
        {{/if}}
        {{/if}}

        {{#if theme_settings.product_outstock_badge}}
            {{#if product.out_of_stock}}
                <div class="sale-flag-side sale-flag-side--outstock">
                    <span class="sale-text">
                        {{#if product.out_of_stock_message}}
                            {{product.out_of_stock_message}}
                        {{else}}
                            {{lang 'products.sold_out'}}
                        {{/if}}
                    </span>
                </div>
            {{/if}}
        {{/if}}
        {{#or product.price.non_sale_price_with_tax product.price.non_sale_price_without_tax}}
            {{#if theme_settings.product_sale_badges '===' 'sash'}}
                <div class="sale-flag-sash">
                    <span class="sale-text">{{lang 'products.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'topleft'}}
                <div class="sale-flag-side">
                    <span class="sale-text">{{lang 'products.on_sale'}}</span>
                </div>
            {{else if theme_settings.product_sale_badges '===' 'burst'}}
                <div class="starwrap">
                    <div class="sale-flag-star"></div>
                    <div class="sale-text-burst">{{lang 'products.on_sale'}}</div>
                </div>
            {{/if}}
        {{/or}}

        {{#if theme_settings.product_custom_badges}}
            {{#each product.custom_fields}}
                {{#if name '===' '__badge'}}
                    <div class="sale-flag-side sale-flag-side--custom">
                        <span class="sale-text">{{{value}}}</span>
                    </div>
                {{/if}}
            {{/each}}
        {{/if}}


        {{#or (if product.images.length '>' 1) product.videos.list}}
        <ul class="productView-imageCarousel-nav" id="productView-imageCarousel-nav-{{product.id}}" data-image-gallery-nav {{#if theme_settings.productpage_thumbnails_slider}}data-image-gallery-nav-slides="{{theme_settings.productpage_thumbnails_count}}"{{/if}} {{#unless theme_settings.productpage_vertical_thumbnails}}data-image-gallery-nav-horizontal="true"{{/unless}}>
            {{#each product.images}}
