components/chiara/sections/categories-li:
{{#or theme_settings.home_cats_id theme_settings.home_cats_count}}
    {{#if theme_settings.home_cats_count '!=' '0'}}
        {{#block "chiara-categories-list"}}
            <div class="chiara-section chiara-section--categoriesList">
                <div class="chiara-categoriesList
                    chiara-categoriesList--count-{{theme_settings.home_cats_count}}
                    {{#if theme_settings.home_cats_show_banner}}chiara-categoriesList--hasBanner{{/if}}
                    {{#if theme_settings.home_cats_style}}chiara-categoriesList--style-{{theme_settings.home_cats_style}}{{/if}}">
                    {{#if theme_settings.home_cats_heading}}
                        <h3 class="page-heading">{{theme_settings.home_cats_heading}}</h3>
                    {{/if}}
                    {{#if theme_settings.home_cats_subheading}}
                        <p class="page-subheading">{{theme_settings.home_cats_subheading}}</p>
                    {{/if}}
                    <div class="chiara-categoriesList-list">
                        {{#if theme_settings.home_cats_id}}
                            {{#if (merge this home_cats_index=0)}}{{/if}}
                            {{#each (split theme_settings.home_cats_id ",")}}
                                {{#each ../categories}}
                                    {{#if id '===' ../this}}
                                        <div class="chiara-categoriesList-item">
                                            {{> components/chiara/sections/categories-list-category category=this theme_settings=../../../theme_settings index=../../../this.home_cats_index}}
                                        </div>
                                        {{#if (merge ../../../this home_cats_index=(add ../../../this.home_cats_index 1))}}{{/if}}
                                    {{/if}}
                                    {{#each children}}
                                        {{#if id '===' ../../this}}
                                            <div class="chiara-categoriesList-item">
                                                {{> components/chiara/sections/categories-list-category category=this theme_settings=../../../../theme_settings index=../../../../this.home_cats_index}}
                                            </div>
                                            {{#if (merge ../../../../this home_cats_index=(add ../../../../this.home_cats_index 1))}}{{/if}}
                                        {{/if}}
                                    {{/each}}
                                {{/each}}
                            {{/each}}
                        {{else}}
                            {{#each (limit categories theme_settings.home_cats_count)}}
                                <div class="chiara-categoriesList-item">
                                    {{> components/chiara/sections/categories-list-category category=this theme_settings=../theme_settings index=@index}}
                                </div>
                            {{/each}}
                        {{/if}}
                    </div>
                </div>
            </div>
        {{/block}}
    {{/if}}
{{/or}}

components/chiara/products/carousel:
{{!
    Parameters:
    - products
    - columns
    - columns_sm
    - columns_md
}}
<section class="productCarousel"
    data-slick='{
        "dots": false,
        "arrows": true,
        "infinite": true,
        "mobileFirst": true,
        "slidesToShow": {{#if columns_sm}}{{columns_sm}}{{else}}2{{/if}},
        "slidesToScroll": 1,
        "autoplay": true,
        "autoplaySpeed": 3000,
        "useTransform": false, {{! Fix for IE10+ }}
        "responsive": [
            {
                "breakpoint": 1260,
                "settings": {
                    "slidesToScroll": 1,
                    "slidesToShow": {{ columns }}
                }
            },
            {
                "breakpoint": 800,
                "settings": {
                    "slidesToScroll": 1,
                    "slidesToShow": {{ columns }}
                }
            },
            {
                "breakpoint": 550,
                "settings": {
                    "slidesToScroll": 1,
                    "slidesToShow": {{#if columns_sm}}{{add (toInt columns_sm) 1}}{{else}}{{ columns }}{{/if}}
                }
            }
        ]
    }'
    {{#if settings.data_tag_enabled}} data-list-name="{{{list}}}"{{/if}}>
    {{#each products}}
    <div class="productCarousel-slide">
        {{>components/products/card show_rating=../settings.show_product_rating settings=../settings theme_settings=../theme_settings customer=../customer show_wishlist=../settings.show_wishlist event="list" urls=../urls}}
    </div>
    {{/each}}
</section>

components/chiara/products/grid:
{{!
    Parameters:
    - products
    - columns
    - columns_sm
    - columns_md
}}
<ul class="productGrid
    {{#if columns}} productGrid--maxCol{{columns}}{{/if}}
    {{#if columns_sm}} productGrid--sm-maxCol{{columns_sm}} productGrid--md-maxCol{{add (toInt columns_sm) 1}}{{/if}}
    "
    {{#if settings.data_tag_enabled}} data-list-name="{{list}}"{{/if}}>
    {{#each products}}
    <li class="product">
        {{>components/products/card show_compare=../show_compare show_rating=../settings.show_product_rating settings=../settings theme_settings=../theme_settings customer=../customer show_wishlist=../settings.show_wishlist event="list" urls=../urls}}
    </li>
    {{/each}}
</ul>

